  genres = ['Genre','Genre1', 'Genre2', 'Genre3'];


  getNovelsByGenre(genre: String) {
    let temp: Novel[] = [];
    this.allNovels.forEach((novel) => {
      if (novel.genre === genre) {
        temp.push(novel);
      }
    });
    return temp;
  }
  getAllNovelsByGenre() {
    let allNovelsByGenre: NovelsByGenre[] = [];
    this.genres.forEach((genre) => {
      let novelsByGenre: NovelsByGenre = {
        title: genre,
        novels: this.getNovelsByGenre(genre),
      };
      allNovelsByGenre.push(novelsByGenre);
    });
    return allNovelsByGenre;
  }
  getNovelsSearched(searchValue: string, searchParam:string){
    let novels: Novel[]=[]
    if(searchParam){
      this.getNovelsByGenre(searchParam).forEach(
        (novel)=>{
          if(novel.id.toLocaleLowerCase().includes(searchValue)){
            novels.push(novel)
          }
        }
      )
    }
    else{
      this.allNovels.forEach(
        (novel)=>{
          if(novel.id.toLocaleLowerCase().includes(searchValue)){
            novels.push(novel)
          }
        }
      )
    }
    return novels
  }
